    public static ArrayList<Movie> getData(){
        ArrayList<Movie> list = new ArrayList<>();
        for (int i = 0; i < judul.length; i++){
            Movie movie = new Movie();
            movie.setJudul(judul[i]);
            movie.setDurasi(durasi[i]);
            movie.setGenre(genre[i]);
            movie.setRilis(rilis[i]);
            movie.setRate(rate[i]);
            movie.setRatecount(ratecount[i]);
            movie.setShortdesc(shortdesc[i]);
            movie.setSinopsis(sinopsis[i]);
            movie.setPoster(poster[i]);
            movie.setTrailer(trailer[i]);
            list.add(movie);
        }
        return list;
    }

